#pragma config(Motor,  motor1,          L1,            tmotorVexIQ, PIDControl, encoder)
#pragma config(Motor,  motor2,          L2,            tmotorVexIQ, openLoop)
#pragma config(Motor,  motor3,          R1,            tmotorVexIQ, PIDControl, encoder)
#pragma config(Motor,  motor4,          R2,            tmotorVexIQ, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#define vexCompetitionState (nVexRCReceiveState & (vrDisabled | vrAutonomousMode))
#pragma competitionControl(Competition)
#pragma autonomousDuration(15)
#pragma userControlDuration(150)
#include "VEX_Competition_Includes.c"

void pre_auton ()
{
}
task autonomous ()
{
}
task usercontrol()
{
	while (1==1){
		//Right joystick controls right wheels
		motor[R1] = vexRT[Ch3];
		motor[R2] = vexRT[Ch3];
		//Left joystick controls left wheels
		motor[L1] = vexRT[Ch2];
    motor[L2] = vexRT[Ch2];

		if(vexRT[Btn8D]){
			startTask(autonomous);
			wait1Msec(15000);
			stopTask(autonomous);
		}
		/* press the bottom button of the right buttons to
		start the autonomous*/
	}
}
